@Throws(NoEmbedException::class)
fun getData(url: String): NoEmbed {
    val resource = NoEmbedResource()
    return resource.get(url)
}

/**
 * Determines whether the passed in url is one of the noEmbed providers
 */
fun containsNoEmbedUrl(url: String): Boolean {
    val regex = Regex(REGEX_STRING)
    return regex.containsMatchIn(url)
}
